import pickleimport streamlit as stdef app():    ###### Show publications list.        st.write("""    ## Publications Using Data from FRIEND        Below is a list of publications that use data from FRIEND. Click on \        the citation if you're interested in accessing the research.    """)        # Print the pubs with links to them.    with open('./data/publications.pickle','rb') as read_file:                pubs_lst = pickle.load(read_file)    st.write("\n\n".join(pubs_lst))